/* Menü ********************************************************/

button
{
position: relative;
font-family: serif;
padding-top: 15px;
padding-left: 0;
padding-right: 0;
margin-right: 2%;
}

.btnAktiv
{
font-size: 22px;
color: #ffb619;
height: 50px;
outline: none;
border: none;
background: none;
}

.btnAktiv:after
{
content: "";
display: block;
position: absolute;
top: 50px;
left: 50%;
z-index: 10;
font-size: 0;
line-height: 0;
height: 0;
padding: 0;
margin: 0;
transform: translateX(-50%);
border: 15px solid #000;
border-right-color: transparent;
border-bottom-color: transparent;
border-left-color: transparent;
}

.btnInaktiv,
.tab
{
font-size: 22px;
color: #fff;
height: 50px;
z-index: 0;
outline: none;
border: none;
background: none;
transition: color 0.3s ease;
}

.btnInaktiv:hover,
.tab:hover
{
color: #ffb619;
cursor: pointer;
}

.divHide
{
display: none;
}

.printHeading
{
display: none;
page-break-before: always;
}

@media print
{
    .divHide {
        display: block!important;
    }
    .printHeading {
        display: block!important;
    }
}

